import React from "react";
import { COLORS, LOCATIONS } from "../constants/index.ts";
import Invitation from "../assets/Invitation.png";
import Church from "../assets/church.svg";
import Link from "../assets/link-svg.svg";
import Dinner from "../assets/Dinner.svg";
import './MainStyle.css';

// Event data interface for type safety
interface EventData {
  id: string;
  title: string;
  location: string;
  time: string;
  icon: string;
  iconAlt: string;
}

// Event data configuration
const eventData: EventData[] = [
  {
    id: 'ceremony',
    title: '–¶–µ—Ä–µ–º–æ–Ω—ñ—è –≤—ñ–Ω—á–∞–Ω–Ω—è',
    location: LOCATIONS.ceremony,
    time: '14:00',
    icon: Church,
    iconAlt: 'Church ceremony icon'
  },
  {
    id: 'reception',
    title: '–ë–µ–Ω–∫–µ—Ç',
    location: LOCATIONS.reception,
    time: '14:00-20:00',
    icon: Dinner,
    iconAlt: 'Dinner reception icon'
  }
];

// Reusable Event Card Component
interface EventCardProps {
  event: EventData;
}

const EventCard: React.FC<EventCardProps> = ({ event }) => {
  const handleMapLinkClick = () => {
    window.open(LOCATIONS.mapsUrl, '_blank', 'noopener noreferrer');
  };

  return (
    <div className="event-card">
      <div className="event-header">
        <img 
          src={event.icon} 
          alt={event.iconAlt}
          className="event-icon"
        />
        <h3 className="event-title">{event.title}</h3>
      </div>

      <div className="event-details">
        <div className="event-detail">
          <span className="event-label">–õ–æ–∫–∞—Ü—ñ—è:</span>
          <span className="event-value">{event.location}</span>
        </div>

        <div className="event-detail">
          <span className="event-label">–ß–∞—Å:</span>
          <span className="event-value">{event.time}</span>
        </div>

        <button 
          className="map-link" 
          onClick={handleMapLinkClick}
          aria-label={`–í—ñ–¥–∫—Ä–∏—Ç–∏ –∫–∞—Ä—Ç—É –¥–ª—è ${event.title}`}
        >
          <img src={Link} alt="Map link icon" className="link-icon" />
          <span>Google Maps</span>
        </button>
      </div>
    </div>
  );
};

// Calendar Event Handler
const createGoogleCalendarEvent = (): void => {
  const url = new URL("https://calendar.google.com/calendar/render");
  url.searchParams.set("action", "TEMPLATE");
  url.searchParams.set("text", "–ù–∞—à–µ –≤–µ—Å—ñ–ª–ª—è üíç");
  url.searchParams.set("details", "–ó–∞–ø—Ä–æ—à—É—î–º–æ –≤–∞—Å —Ä–æ–∑–¥—ñ–ª–∏—Ç–∏ –∑ –Ω–∞–º–∏ —Ü–µ–π –æ—Å–æ–±–ª–∏–≤–∏–π –¥–µ–Ω—å!");
  url.searchParams.set("location", "–ø—Ä–æ–≤.–®–∫—ñ–ª—å–Ω–∏–π 2–∞, —Å.–•—É—Ç–æ—Ä–∏");
  url.searchParams.set("dates", "20251004T140000Z/20251004T170000Z");
  window.open(url.toString(), "_blank");
};

// Main Component
export const Main: React.FC = () => {
  return (
    <div className="main-container">
      <div 
        className="background-image"
        style={{ backgroundImage: `url(${Invitation})` }}
      >
        <div className="content-wrapper">
          {/* Header Section */}
          <header className="schedule-header">
            <h1 className="schedule-title">–†–æ–∑–∫–ª–∞–¥ –≤–µ—Å—ñ–ª–ª—è</h1>

            <div className="decorative-line-mobile">
              <hr className="decorative-line decorative-line--short" style={{ backgroundColor: COLORS.accent }} />
            </div>

            <div className="decorative-line-desktop">
              <hr className="decorative-line decorative-line--long" style={{ backgroundColor: COLORS.accent }} />
            </div>
          </header>

          {/* Events Section */}
          <section className="events-section">
            <div className="events-container">
              {eventData.map((event) => (
                <EventCard key={event.id} event={event} />
              ))}
            </div>
          </section>

          {/* Calendar Button */}
          <div className="calendar-section">
            <button 
              className="calendar-button"
              onClick={createGoogleCalendarEvent}
              aria-label="–î–æ–¥–∞—Ç–∏ –≤–µ—Å—ñ–ª–ª—è –¥–æ Google –ö–∞–ª–µ–Ω–¥–∞—Ä—è"
            >
              –î–æ–¥–∞—Ç–∏ –≤ Google –ö–∞–ª–µ–Ω–¥–∞—Ä
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
