import React from "react";
import { COLORS, LOCATIONS } from "../constants/index.ts";
import Invitation from "../assets/Invitation.png";
import Church from "../assets/church.svg";
import Link from "../assets/link-svg.svg";
import Dinner from "../assets/Dinner.svg";
import "./Main.css";

interface EventProps {
    icon: string;
    iconAlt: string;
    title: string;
    location: string;
    time: string;
    mapsUrl: string;
    className?: string;
}

const Event: React.FC<EventProps> = ({
    icon,
    iconAlt,
    title,
    location,
    time,
    mapsUrl,
    className = "",
}) => (
    <div className={`event ${className}`}>
        <div className="event__header">
            <img className="event__icon" src={icon} alt={iconAlt} />
            <h3 className="event__title">{title}</h3>
        </div>
        <div className="event__details">
            <strong>–õ–æ–∫–∞—Ü—ñ—è: </strong>
            {location}
        </div>
        <div className="event__time">
            <strong>–ß–∞—Å: </strong>
            {time}
        </div>
        <div className="event__link">
            <img src={Link} alt="Link icon" className="event__link-icon" />
            <a
                href={mapsUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="event__link-text"
            >
                <strong>Google Maps</strong>
            </a>
        </div>
    </div>
);

const createGoogleCalendarEvent = (): void => {
    const url = new URL("https://calendar.google.com/calendar/render");
    url.searchParams.set("action", "TEMPLATE");
    url.searchParams.set("text", "–ù–∞—à–µ –≤–µ—Å—ñ–ª–ª—è üíç");
    url.searchParams.set(
        "details",
        "–ó–∞–ø—Ä–æ—à—É—î–º–æ –≤–∞—Å —Ä–æ–∑–¥—ñ–ª–∏—Ç–∏ –∑ –Ω–∞–º–∏ —Ü–µ–π –æ—Å–æ–±–ª–∏–≤–∏–π –¥–µ–Ω—å!"
    );
    url.searchParams.set("location", "–ø—Ä–æ–≤.–®–∫—ñ–ª—å–Ω–∏–π 2–∞, —Å.–•—É—Ç–æ—Ä–∏");
    url.searchParams.set("dates", "20251004T140000Z/20251004T170000Z");
    window.open(url.toString(), "_blank");
};

export const Main: React.FC = () => {
    return (
        <div className="main-container">
            <div
                className="main-background"
                style={{ backgroundImage: `url(${Invitation})` }}
            >
                <header className="schedule-header">
                    <h2 className="schedule-title">–†–æ–∑–∫–ª–∞–¥ –≤–µ—Å—ñ–ª–ª—è</h2>
                    <hr className="decorative-line decorative-line--mobile" />
                    <hr className="decorative-line decorative-line--desktop" />
                </header>

                <main className="events-container">
                    <Event
                        icon={Church}
                        iconAlt="Church ceremony icon"
                        title="–¶–µ—Ä–µ–º–æ–Ω—ñ—è –≤—ñ–Ω—á–∞–Ω–Ω—è"
                        location={LOCATIONS.ceremony}
                        time="14:00"
                        mapsUrl={LOCATIONS.mapsUrl}
                    />

                    <Event
                        icon={Dinner}
                        iconAlt="Dinner reception icon"
                        title="–ë–µ–Ω–∫–µ—Ç"
                        location={LOCATIONS.reception}
                        time="14:00-20:00"
                        mapsUrl={LOCATIONS.mapsUrl}
                        className="event--banquet"
                    />
                </main>

                <footer className="calendar-section">
                    <button
                        className="calendar-button"
                        onClick={createGoogleCalendarEvent}
                        aria-label="Add wedding event to Google Calendar"
                    >
                        –î–æ–¥–∞—Ç–∏ –≤ Google –ö–∞–ª–µ–Ω–¥–∞—Ä
                    </button>
                </footer>
            </div>
        </div>
    );
};
